function diariesReceived(diaries) {
    return {
        type: GET_DIARIES,
        diaries: diaries
    }
}

//GET Diary 
export const getDiaries = diaries => dispatch => {
    fetch('api/diary')
      .then((res) => res.json())
      .then( (data) => dispatch(diariesReceived(data.data)))
      .catch((err) => console.log(err))
}

// ADD Diary 

export const addDiary = diary => dispatch => {
    axios
       .post('/api/diary', diary)
       .then(res => 
        dispatch({
            type: ADD_DIARY,
            payload: res.data
        })
        )
}
