import { GET_DIARY, DELETE_DIARY, ADD_DIARY } from '../actions/types';

const initialState = {
        diaries: [
            {  
               id: 1,
               title: 'Hi',
               content: 'hoho',
               selectedHeart: 1, 
               date: new Date(),
               bodyChecked: 'No'
            },
            {  
                id: 2,
                title: 'Bye',
                content: 'hehe',
                selectedHeart: 4, 
                date: new Date(),
                bodyChecked: 'Yes'
             }
        ]

}

export default function(state = initialState, action) {
   switch(action.type) {
       case GET_DIARY:
           console.log(action);
           return {
               ...state
           }
       case DELETE_DIARY:
           return {
               ...state,
               diaries: state.diaries.filter(diary => diary.id !== action.payload)
           }
       case ADD_DIARY:
           return {
               ...state, 
               diaries: [action.payload, ...state.diaries]
           }
           default:
               return state;
   }
}
