import axios from 'axios';
import { GET_DIARY, DELETE_DIARY, ADD_DIARY, DIARY_LOADING } from '../actions/types';


export const getDiary = () => dispatch => {
    dispatch(setDiaryLoading());
    axios
       .get('http://localhost:3001/api/diary/diaries')
       .then(res => 
        dispatch({
            type: GET_DIARY,
            payload: res.data
        })
        )
}

export const deleteDiary = id => {
    return {
        type: DELETE_DIARY,
        payload: id
        }
}

export const addDiary = diary => {
    return {
        type: ADD_DIARY,
        payload: diary
    }
}

export const setDiaryLoading = () => {
     return {
         type: DIARY_LOADING
     }
}
