import React from 'react'
import {HiOutlineHeart} from 'react-icons/hi'
import "./HeartRating.css"


export default function HeartRating(props) {
    const {
        index, 
        selectedHeart,
        hoverHeart, 
        onMouseEnter, 
        onMouseLeave,
        onSaveHeart

    } = props;



    return (
        <div>
            <iOutlineHeart
              index={index}
              className={index <= (hoverHeart || selectedHeart) ? "selectedHeart" : "heart"}
              onMouseEnter={onMouseEnter}
              onMouseLeave={onMouseLeave}
              onClick={onSaveHeart}
              hoverHeart={hoverHeart}
            />

        </div>

    )
}
