import axios from 'axios';
import { ADD_DIARY } from './types';

//add Diary
export const addDiary = (
    title, 
    content, 
    date, 
    selectedHeart, 
    bodyChecked
    ) => async(dispatch) => {
        try {
            const { data } = await axios
            .post("/api/diary/diaries/new", {title, 
                content, 
                date, 
                selectedHeart, 
                bodyChecked})
            dispatch({
                type: ADD_DIARY,
                payload: data
            })

        }
        catch (error) {
            console.log(error.response.data)
        }
    }
