export const fetchDiartStart = () => ({
    type: FETCH_DIARY_START
  });

  export const fetchDiarySuccess = diaries => ({
    type: FETCH_DIARY_SUCCESS,
    payload: diaries
  });

  export const fetchDiaryFailure = error => ({
    type: FETCH_DIARY_FAILURE,
    payload: error
  });

  export const fetchDiaryStartAsync = () => {
    return dispatch => {
      dispatch(fetchDiaryStart());
      axios
        .get('/api/diary')
        .then(response => dispatch(fetchDiarySuccess(response.data.data)))
        .catch(error => dispatch(fetchDiaryFailure(error)));
    };
  };



export const deleteDiary = id => dispatch => {
    axios.delete(`/api/diary/${id}`)
         .then(res => 
            dispatch({
                type: DELETE_DIARY,
                payload: id
            }))

}

export const addDiary = diary => dispatch => {
    axios
       .post('/api/diary', diary)
       .then(res => 
        dispatch({
            type: ADD_DIARY,
            payload: res.data
        })
        )

}
