import React from 'react'
import {HiOutlineHeart} from 'react-icons/hi'
import "./HeartRating.css"

export default function HeartRating({selectedHeart, setSelectedHeart, totalHearts, hovered, setHovered}) {

    return (
        <div>
            {[...Array(totalHearts)].map((n, i) => {
                i += 1
            return 
            <HiOutlineHeart
              key={i}
              className={i <= (hovered || selectedHeart) ? "selectedHeart" : "heart"}
              onMouseEnter={()=> setHovered(i)}
              onMouseLeave={() => setHovered(selectedHeart)}
              onClick={() => setSelectedHeart(i)}

            />

            })}


        </div>
    )
}
