import React, { Component } from 'react';
import moment from 'moment';
// import axios from 'axios';
import HeartRating from "./HeartRating.js"
import Navbar from "../components/Navbar"
import './Diaries.css'

import { connect } from 'react-redux';
import { getDiaries } from '../actions/diaryAction';
import { PropTypes } from 'prop-types';


class Diaries extends Component {

    componentDidAmount() {
        this.props.getDiaries();
    }
    // deleteDiary = (id) => {
    //     axios.delete('/delete/' + id)
    //     alert('item deleted')
    //     console.log(`deleted item is ${id}`)
    //  } 

    // onDeleteClick = (id) => {
    //     this.props.deleteDiary(id)
    // }

   render() {
       const { diaries } = this.props.diary;

       return(
        <div>
        <Navbar />
        <div className="diary-container">
      <h1 className="diary-title">forMe.</h1>
      <div>

          {diaries.map(({_id, title, content, date, bodyChecked, selectedHeart})=> 
          <>
      <div className="diary">
        <div className="diary-item">
        <div className="diary-grid">
        <div>
        <h2>{title}</h2>
        </div>
        <div className="grid-flex">

        <HeartRating 
         totalHearts={5} 
         selectedHeart={selectedHeart}
        //  setSelectedHeart={this.props.selectedHeart}
         />

         </div>
        </div>

        <div className="diary-grid">
        <div>
        {date ? moment(date).format('l') : null}
        </div>
        <div className="grid-flex">
         <h4>Treat your body well?</h4>
         <p>{bodyChecked}</p>
         </div>
        </div>
        </div>

         <div className="diary-item">
         <p>{content}</p>
         </div>
         <div className="diary-item">
         {/* <button onClick={this.onDeleteClick.bind(this, id)} className="sm-btn" >
         Delete
        </button> */}
         </div>

        </div>


        </> 

)}
</div>

</div>  
       </div>

       )
   }
   };

   Diaries.propTypes = {
       getDiaries: PropTypes.func.isRequired, 
       diary: PropTypes.object.isRequired
   }

   const mapStateToProps = (state) => ({
       diary: state.diary

   })


export default connect(mapStateToProps, { getDiaries })(Diaries);
