import React, { useState, useEffect } from 'react';
import { connect } from 'react-redux'
import { useDispatch } from 'react-redux'
import { useHistory, Redirect } from 'react-router-dom'
import { Link } from 'react-router-dom';
import { register } from '../../actions/authAction'
import { Modal, NavLink, ModalHeader, ModalBody } from 'reactstrap'
import './Register.css';

function Register({isAuthenticated, error, token}) {

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [msg, setMsg] = useState(null);


  const dispatch = useDispatch();
  const history = useHistory();

  // function errorHandle() {
  //   if(error.id === 'REGISTER_FAIL') {
  //     setMsg(error.msg.msg)
  //   } else {
  //     setMsg(null)
  //     history.push('/')
  //   };
  // }

  //check errors 
  // useEffect(() => {
  //   errorHandle();
  //  }, [error])

  const onSubmit = e => {
    e.preventDefault();

      const newUser = {
        firstName, 
        lastName, 
        email,
        password
      }

      console.log(newUser);
      dispatch(register(newUser));


        setFirstName('')
        setLastName('')
        setEmail('')
        setPassword('')
        setMsg(null)

        e.target.reset();

    };



    return (
        <div>
        <Modal>
           <div className="register-container">
           <form className="register-form" onSubmit={onSubmit}>
           <div className="regi-title">
           <h1>Register</h1>
           </div>
           {msg ? <div className="alert">{msg}</div> : null}
           <div className="regi-form"> 
                 <input
                 className="regi-item"
                 placeholder="First Name"
                 value={firstName}
                 onChange={event => setFirstName(event.target.value)}
                 />
                 <input
                 className="regi-item"
                 placeholder="Last Name"
                 value={lastName}
                 onChange={event => setLastName(event.target.value)}
                 />
                 <input
                 className="regi-item"
                 type="email"
                 placeholder="Your E-mail"
                 value={email}
                 onChange={event => setEmail(event.target.value)}
                 />
                <input
                 className="regi-item"
                 type="password"
                 placeholder="Password"
                 value={password}
                 onChange={event => setPassword(event.target.value)}
                />
                 <button className="regi-btn" type="submit" >Sign Up</button>
                 <div className="log-in">
                 <span>Already registered?</span>
                 <Link to="/">
                 <button>Log-in</button>
                 </Link>

                </div>
                 </div>
            </form>
           </div>
       </Modal>
        </div>
    )
}

const mapStateToProps = state => ({
  isAuthenticated: state.auth.isAuthenticated,
  error: state.error

})

export default connect(mapStateToProps, {register})(Register);
