import React, { useState }from 'react'
import {IoMdHeartEmpty} from 'react-icons/io'
import "./HeartRating.css"

export default function HeartRating( {totalHearts}) {
    const [selectedHeart, setSelectedHeart] = useState(0);

    const handleHover = () => {
        setSelectedHeart(selectedHeart);

    }

    return (
        <div>
            {[...Array(totalHearts)].map((n, i) => {
            return <IoMdHeartEmpty
              key={i}
              className={selectedHeart ? "selectedHeart" : "Heart"}
              onMouseEnter={()=> handleHover()}
              value={selectedHeart}
            //   onMouseLeave={()=> handleHoverOut()}
            //   onClick={() =>  handleClick()}
            />
            })}


        </div>
    )
}
