import { GET_DIARIES } from '../actions/types';

const initialState = {
    diaries: [{
        title: '',
        content: '',
        selectedHeart: 0, 
        date: null,
        bodyChecked: ''
    }],
};


export default function(state = initialState, action) {
   switch(action.type) {
       case GET_DIARIES:
           return {
               ...state,
               diaries: action.payload
           }
        default: 
           return state;
   }
}
