import { GET_DIARY, DELETE_DIARY, ADD_DIARY, DIARY_LOADING } from '../actions/types';

const initialState = {
        diaries: [],
        loading: false

}

export default function(state = initialState, action) {
   switch(action.type) {
       case GET_DIARY:
           console.log(action);
           return {
               ...state
           }
       case DELETE_DIARY:
           return {
               ...state,
               diaries: state.diaries.filter(diary => diary.id !== action.payload)
           }
       case ADD_DIARY:
           return {
               ...state, 
               diaries: [action.payload, ...state.diaries]
           }
       case DIARY_LOADING:
           return {
               ...state,
               loading: true
           }
           default:
               return state;
   }
}
