import {IoMdHeart,IoMdHeartEmpty} from 'react-icons/io'


export default function heartRate({ totalHearts }) {
    const [heartSelected, selectHeart] = useState(0);

    const Heart = ({selected = false, onClick = f => f }) => (
        <div>
          {selected? <IoMdHeart /> : <IoMdHeartEmpty /> } onClick = {onClick}
        </div>
    )


    return (
        <div>
            {[...Array(totalHearts)].map((n, i) => (
                <Heart
                 key={i}
                 selected={i < heartSelected}
                 onClick={() => selectHeart(i+1)}
                 />
            ))}
