import React, { useState }from 'react'
import {IoMdHeartEmpty} from 'react-icons/io'
import "./HeartRating.css"

export default function HeartRating( {totalHearts}) {
    const [isHover, setIsHover] = useState(false);
    const [selectedHeart, setSelectedHeart] = useState(0);

    return (
        <div>
            {[...Array(totalHearts)].map((n, i) => {
            return <IoMdHeartEmpty
              key={i}
              className={isHover ? "selectedHeart" : "Heart"}
              onMouseEnter={()=> setIsHover(true)}
              onMouseLeave={()=> setIsHover(false)}
              isHover={i < selectedHeart}
              onClick={() =>  setSelectedHeart(i + 1)}
            />
            })}


        </div>
    )
}
