import { startOfSecond } from 'date-fns';
import { GET_DIARY, DELETE_DIARY } from '../actions/types';

const initialState = {
        diaries: [
            {  
               id: 1,
               title: 'Hi',
               content: 'hoho',
               selectedHeart: 1, 
               date: new Date(),
               bodyChecked: 'No'
            }
        ]

}

export default function(state = initialState, action) {
   switch(action.type) {
       case GET_DIARY:
           console.log(action);
           return {
               ...state
           }
       case DELETE_DIARY:
           return {
               ...state,
               diaries: state.diaries.filter(diary => diary.id !== action.payload)
           }
           default:
               return state;
   }
}
