import React from 'react'
import Heart from './Heart.js'
import "./HeartRating.css"

export default function HeartRating({selectedHeart, setSelectedHeart, totalHearts}) {
     const [value, setValue] = useState(0)

    function handleMouseEnter() {
        setValue(value)
    }

    function handleMouseLeave() {
        setValue(0)
    }

    function handleClick() {
        setValue(value)
    }

    return (
        <div>
            {[...Array(totalHearts)].map((n, i) => {
                i += 1
            return <Heart
              key={i}
              value={i}
              className={i <= (selectedHeart) ? "selectedHeart" : "heart"}
              handleHover={handleMouseEnter}
              handleHoverLeave={handleMouseLeave}
              handleClick={handleClick}
            />

            })}


        </div>
    )
}
