import axios from 'axios';
import { GET_DIARIES, DELETE_DIARY, ADD_DIARY } from '../actions/types';


export const getDiaries = (diaries) => {
    return {
        type: GET_DIARIES, 
        diaries
    }
}

export const getAllDiaries = () => {
  return (dispatch) => {
      return axios.get('/api/diary')
        .then(res => {
            dispatch(getDiaries(res.data))
        })
        .catch(error => {
            throw(error);
        })
  }
}


export const deleteDiary = id => {
    return {
        type: DELETE_DIARY,
        payload: id
        }
}

export const addDiary = diary => dispatch => {
    axios
       .post('/api/diary', diary)
       .then(res => 
        dispatch({
            type: ADD_DIARY,
            payload: res.data
        })
        )

}

export const setDiaryLoading = () => {
     return {
         type: DIARY_LOADING
     }
}
