import React from 'react'
import {HiOutlineHeart} from 'react-icons/hi'
import "./HeartRating.css"


export default function HeartRating({index, 
    selectedHeart,
    hoverHeart, 
    setHoverHeart, 
    setSelectedHeart,
    totalHearts
    }) {
        return (
            <div>
            {[...Array(totalHearts)].map((n, i) => {
                i += 1
                return <HiOutlineHeart
                  key={i}
                  className={i <= (hoverHeart || selectedHeart) ? "selectedHeart" : "heart"}
                  onMouseEnter={setHoverHeart(i)}
                  onMouseLeave={setHover(selectedHeart)}
                  onClick={setSelectedHeart(i)} />


                })}
            </div>

        )

    }
