import { fetchDiaryPending, fetchDiarySuccess, fetchDiaryError } from './diaryAction';

function fetchDiaries() {
    return dispatch => {
        dispatch(fetchDiaryPending());
        fetch('http://localhost:5000/api/diary')
        .then(res => res.json())
        .then(res => {
            if(res.error) {
                throw(res.error);
            } 
            dispatch(fetchDiarySuccess(res.diaries));
            return res.diaries;
        })
        .catch(error => {
            dispatch(fetchDiaryError(error));
        })
    }
}

export default fetchDiaries;
