import React from 'react'
import {HiOutlineHeart} from 'react-icons/hi'
import "./HeartRating.css"

export default function HeartRating({value, selectedHeart, setSelectedHeart, hover, setHover}) {

    return (
        <div>

            <HiOutlineHeart

              className={value <= (hover || selectedHeart) ? "selectedHeart" : "heart"}
              onMouseEnter={() => setHover(value)}
              onClick={() => setSelectedHeart(value)}
            />



        </div>
    )
}
