import { GET_DIARIES, DELETE_DIARY, ADD_DIARY, DIARY_LOADING, FETCH_DIARY_START, FETCH_DIARY_SUCCESS, FETCH_DIARY_FAILURE  } from '../actions/types';

const initialState = {
        diaries: [],
        loading: false,
        isFetching: false,
        errorMessage: undefined

}

export default function(state = initialState, action) {
   switch(action.type) {
       case GET_DIARIES:
           console.log(action);
           return {
               ...state,
               diaries: action.payload, 
               loading: false
           }
       case DELETE_DIARY:
           return {
               ...state,
               diaries: state.diaries.filter(diary => diary._id !== action.payload)
           }
       case ADD_DIARY:
           return {
               ...state, 
               diaries: [action.payload, ...state.diaries]
           }
       case DIARY_LOADING:
           return {
               ...state,
               loading: true
           }

    case FETCH_DIARY_START:
      return {
        ...state,
        isFetching: true
      };
    case FETCH_DIARY_SUCCESS:
      return {
        ...state,
        diaries: action.payload,
        isFetching: false
      };
    case FETCH_DIARY_FAILURE:
      return {
        ...state,
        isFetching: false,
        errorMessage: action.payload
      };

           default:
               return state;
   }
}

export const getProducts = state => state.products;
export const getProductsPending = state => state.pending;
export const getProductsError = state => state.error;
