import React, { useState } from 'react'
import { connect } from 'react-redux';
import { useHistory } from 'react-router-dom'
import Navbar from '../components/Navbar'
import DatePicker from "react-datepicker";
import HeartRating from "./HeartRating.js"

import { addDiary } from "../actions/diaryActions"

import "./AddDiary.css"
import "react-datepicker/dist/react-datepicker.css";



function AddDiary() {
  const [diary, setDiary] = useState([{
    title: '',
    content: '',
    selectedHeart: 0, 
    date: null,
    bodyChecked: ''
}])
  // const [selectedHeart, setSelectedHeart] = useState(0);
  // const [date, setDate] = useState(null);
  // const [title, setTitle] = useState('');
  // const [content, setContent] = useState('');
  // const [bodyChecked, setBodyChecked] = useState('')


  // const handleChange = (e) => {
  //  setBodyChecked(e.target.value)
  // }

  function handleChange(e) {
    const { name, value } = e.target;
    setDiary(diary => ({...diary, [name]: value}))
  }

  const history = useHistory();

  const onSubmit = (e) => {
    e.preventDefault(); 

    // const newDiary = {
    //   title, 
    //   content, 
    //   date, 
    //   selectedHeart, 
    //   bodyChecked,
    // }  

      addDiary(diary);


    history.push('/diaries');



    // setDate(null)
    // setTitle('')
    // setContent('')
    // setSelectedHeart(0)
    // setBodyChecked('')

    // e.target.reset();
  }

    return (
        <div>
        <Navbar />
           <form className="form" onSubmit={onSubmit}>
           <h1 className="form-title">How are you today? </h1>
            <div className="form-control">
            <div className="form-item">
              <label>SELECT DATE</label>
              <DatePicker selected={diary.date} 
                          onChange={handleChange}
                          name="date"
                          placeholderText="Choose a date" />
            </div>
            <div className="form-item">
              <label>ONE THING I AM GRATEFUL FOR</label>
              <input 
              title={diary.title} 
              onChange={handleChange} 
              name="title"
              type="text" 
              placeholder="Today.."/>
            </div>
            <div className="form-item">
              <label>GIVE YOURSELF HEARTS</label>
              <span>How many hearts would you give to yourself?</span>

            <div className="hearts">
             <HeartRating 
              totalHearts={5} 
              selectedHeart={diary.selectedHeart}
              setSelectedHeart={handleChange}
              name="selectedHeart"
               />
             </div>

            </div>

            <div className="form-item">
              <label>TREAT YOUR BODY WELL?</label>
              <span>Eat Healthy/ Meditation / Exercise </span>
              <div className="checkbox">
              <div className="check-opt">
              <label>YES</label>
                <input 
                type="radio" 
                name="bodyChecked"
                checked={bodyChecked === "Yes"}
                value="Yes" 
                onChange={handleChange}/>
              </div>
              <div className="check-opt">
              <label>NO</label>
              <input 
              type="radio" 
              name="bodyChecked"
              checked={bodyChecked === "No"}
              value="No"
              onChange={handleChange} />
              </div>
              </div>
            </div>
             <div className="form-item">
              <label>TODAY I FEEL</label>
              <textarea 
              content={diary.content} 
              onChange={handleChange} 
              name="Content" 
              placeholder="Tell me about your day! What happened? How do you feel?" 
              cols="15" 
              rows="5" />
             </div>

            </div>

            <div className="form-upload">
            <button type='submit' value= "Upload it!"  className="lg-btn">Upload it!</button> 
            </div>

            </form>

        </div>
    )
}



const mapStateToProps = (state) => ({
  diary: state.diary
});

export default connect(mapStateToProps, { addDiary })(AddDiary);
