import React from 'react'
import {HiOutlineHeart} from 'react-icons/hi'
import "./HeartRating.css"

function Heart(props) {
    const { color = 'none'} = props; 
    return (
         <div>
              <HiOutlineHeart color={color}/>
         </div>
    )
}

function HeartRating(props) {
    const {
        index, 
        seletedHeart,
        hoverHeart, 
        onMouseEnter, 
        onMouseLeave,
        onSaveHeart
    } = props;

    const fill = React.useMemo(() => {
        if (hoverHeart >= index) {
            return 'red';
        } else if (!hoverHeart && selectedHeart >= index) {
            return 'red';
        }
        return 'none';
    }, [selectedHeart, hoverHeart, index])

    return (
        <div 
        onMouseEnter={() => onMouseEnter(index)}
        onMouseLeave={() => onMouseLeave()}
        onClick={() => onSaveHeart(index)}>
        <Heart color={color} />
        </div>
    )
}

export default HearRating;
